import json
turns = []
with open('/home/admin/imagen/.blacksmith/sessions/13.jsonl') as f:
    for line in f:
        line = line.strip()
        if line:
            try:
                turns.append(json.loads(line))
            except:
                pass

# Get all tool calls detail (bash commands run)
assistant_turns = [t for t in turns if t.get('type') == 'assistant']
print('=== TOOL CALLS DETAIL ===')
for i, t in enumerate(assistant_turns):
    msg = t.get('message', {})
    content = msg.get('content', [])
    if isinstance(content, list):
        for c in content:
            if isinstance(c, dict) and c.get('type') == 'tool_use':
                name = c.get('name', '?')
                inp = c.get('input', {})
                if name == 'Bash':
                    cmd = inp.get('command', '')[:100]
                    print(f'Turn {i+1} Bash: {cmd}')
                elif name == 'Read':
                    print(f'Turn {i+1} Read: {inp.get(\"file_path\", \"?\")}')
                else:
                    print(f'Turn {i+1} {name}: {str(inp)[:80]}')
