import sys, json
lines = sys.stdin.readlines()
for i, line in enumerate(lines):
    try:
        data = json.loads(line)
        role = data.get('role', data.get('type', 'unknown'))
        content = data.get('content', '')
        tool_use = []
        text_parts = []
        if isinstance(content, list):
            for c in content:
                if isinstance(c, dict):
                    if c.get('type') == 'tool_use':
                        tool_use.append(c.get('name', ''))
                    elif c.get('type') == 'text':
                        text_parts.append(c.get('text', '')[:100])
        elif isinstance(content, str):
            text_parts.append(content[:100])

        if tool_use:
            print(f'[{i}] {role} TOOLS: {tool_use}')
        elif text_parts:
            text = ' '.join(text_parts)
            if text.strip():
                print(f'[{i}] {role} TEXT: {text[:150]}')
        else:
            print(f'[{i}] {role} (empty)')
    except Exception as e:
        print(f'[{i}] PARSE ERROR: {e}: {line[:100]}')
