import sys, json

lines = sys.stdin.readlines()
events = [json.loads(l.strip()) for l in lines if l.strip()]

# The content is nested in message.content
assistant_msgs = [e for e in events if e.get('type') == 'assistant']
print(f'Total assistant events: {len(assistant_msgs)}')

turns_with_tools = 0
turns_without_tools = 0
tool_names_used = {}
narration_texts = []

for e in assistant_msgs:
    msg = e.get('message', {})
    content = msg.get('content', [])
    if not isinstance(content, list):
        continue
    has_tool = any(isinstance(c, dict) and c.get('type') == 'tool_use' for c in content)
    if has_tool:
        turns_with_tools += 1
        for c in content:
            if isinstance(c, dict) and c.get('type') == 'tool_use':
                name = c.get('name', 'unknown')
                tool_names_used[name] = tool_names_used.get(name, 0) + 1
    else:
        turns_without_tools += 1
        for c in content:
            if isinstance(c, dict) and c.get('type') == 'text':
                narration_texts.append(c.get('text', '')[:150])

print(f'Turns with tools: {turns_with_tools}')
print(f'Narration-only turns: {turns_without_tools}')
print('Tools used:', json.dumps(tool_names_used, indent=2))
if narration_texts:
    print('Narration samples:')
    for t in narration_texts[:3]:
        print(' -', t)
