import sys, json
lines = sys.stdin.readlines()
sessions = []
for line in lines:
    try:
        obj = json.loads(line.strip())
        sessions.append(obj)
    except:
        pass

# Detailed turn analysis - what tools were called and with what args
for i, t in enumerate(sessions):
    if t.get('type') in ('assistant', 'user'):
        content = t.get('message', {}).get('content', []) if t.get('type') == 'assistant' else t.get('message', {}).get('content', t.get('content', []))
        for c in content:
            if isinstance(c, dict):
                if c.get('type') == 'tool_use':
                    name = c.get('name')
                    inp = c.get('input', {})
                    if name == 'Bash':
                        cmd = inp.get('command', '')[:200]
                        print(f'Turn {i} Bash: {cmd}')
                    elif name == 'Read':
                        fp = inp.get('file_path', '')
                        print(f'Turn {i} Read: {fp}')
                elif c.get('type') == 'tool_result':
                    pass
