import json

path = '/home/admin/imagen/.blacksmith/sessions/22.jsonl'
turns = []

with open(path) as f:
    for line in f:
        line = line.strip()
        if not line:
            continue
        try:
            turns.append(json.loads(line))
        except:
            pass

# Show all tool calls with details
turn_num = 0
for msg in turns:
    t = msg.get('type', '')
    if t == 'assistant':
        turn_num += 1
        content = msg.get('message', {}).get('content', [])
        if not isinstance(content, list):
            continue
        for block in content:
            if isinstance(block, dict) and block.get('type') == 'tool_use':
                name = block.get('name', '?')
                inp = block.get('input', {})
                if name == 'Read':
                    print(f'Turn {turn_num}: Read file={inp.get(\"file_path\",\"?\")}')
                elif name == 'Bash':
                    cmd = inp.get('command', '')[:200]
                    print(f'Turn {turn_num}: Bash cmd={cmd}')
"
